	size_x       = 248     -- Breite der Anzeige
	size_y       = 248     -- HÃ¶he der Anzeige
	timeout      = 30      -- Wartezyklen (jeweils 500 ms) 

function getdata(Url,outputfile)
	if Url == nil then return nil end
	if Curl == nil then
		Curl = curl.new()
	end

	local ret, data = Curl:download{url=Url,A="Mozilla/5.0;",maxRedirs=5,followRedir=true,o=outputfile }
	if ret == CURL.OK then
		if outputfile then
			return 1
		end
		return data
	else
		return nil
	end
end

-- character table string

local b='ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/'

-- decode
function dec(data)
	data = string.gsub(data, '[^'..b..'=]', '')
	return (data:gsub('.', function(x)
    	if (x == '=') then return '' end
    	local r,f='',(b:find(x)-1)
    	for i=6,1,-1 do r=r..(f%2^i-f%2^(i-1)>0 and '1' or '0') end
    	return r;
	end):gsub('%d%d%d?%d?%d?%d?%d?%d?', function(x)
    	if (#x ~= 8) then return '' end
    	local c=0
    	for i=1,8 do c=c+(x:sub(i,i)=='1' and 2^(8-i) or 0) end
    	return string.char(c)
	end))
end

function decodeImage(b64Image)
	local imgTyp = b64Image:match("data:image/(.-);base64,")
	local repData = "data:image/" .. imgTyp .. ";base64,"
	local b64Data = string.gsub(b64Image, repData, "");

	local tmpImg = os.tmpname()
	local retImg = tmpImg .. "." .. imgTyp
--	os.rename(tmpImg .. "." .. imgTyp, "sleep." .. imgTyp)

	local f = io.open(retImg, "w+")
	f:write(dec(b64Data))
	f:close()
	os.remove(tmpImg)

	return retImg
end

function main()
	modus = getdata('http://127.0.0.1/control/getmode')
        sleep = decodeImage("data:image/png;base64,")
        radio = decodeImage("data:image/png;base64,")
	n     = neutrino()
		size_x = size_x
		size_y = size_y
		x_beg  = SCREEN.OFF_X
		y_beg  = SCREEN.OFF_Y
		x_end  = SCREEN.END_X - size_x
		y_end  = SCREEN.END_Y - size_y

	n:StopPicture()               -- Bildschirm wird geleert, d.h. ein schwarzer Hintergrund wird erzeugt

	if string.find(modus,'tv') -- webtv ??
			then
			        p = cpicture.new{x=SCREEN.OFF_X, y=SCREEN.OFF_Y, dx=size_x, dy=size_y, image= sleep}
			        os.execute('pzapit -mute')    -- Stummschaltung
			else
			        p = cpicture.new{x=SCREEN.OFF_X, y=SCREEN.OFF_Y, dx=size_x, dy=size_y, image= radio}
		end
	repeat
		if  size_x == size_x
		then
			start_x = math.random(x_beg, x_end)
			start_y = math.random(y_beg, y_end)
			p:hide()
			n:PaintBox(0,0,-1,-1,COL.BLACK )
		if string.find(modus,'tv')
			then
			        p = cpicture.new{x = start_x, y = start_y, dx = size_x, dy = size_y, image = sleep}
			else
			        p = cpicture.new{x = start_x, y = start_y, dx = size_x, dy = size_y, image = radio}
		end
			p:paint()
		end
			i = 0
		repeat
			i = i + 1
			msg, data = n:GetInput(500)
			until msg == RC.ok or msg == RC.home or i == timeout -- mit RC.ok erfolgt ein vorzeitiger Positionswechsel des Bildes 

		until msg == RC['home']  -- Bildschrimschonermodus wird damit beendet 

	os.execute('pzapit -unmute')     -- Ton wird damit wieder hÃ¶rbar aktiviert
	os.execute("pzapit -rz")         -- Um das laufende (Web-)TV- bzw. das (Web-)Radiohintergrundbild wieder anzuzeigen, wird ein Rezap durchgefÃ¼hrt
end

main()
